import React, { useState } from 'react';
import data from './data.json';
import { Heading } from '../Heading/Heading';
import { Text } from '../Text/Text';
import Style from '../Departments/Departments.module.css';

export const Departments = () => {
  const [DepartmentJson, getDepartmentJson] = useState(data);
  return (
    <div class={Style.departmentDiv}>
      <Heading>Departments</Heading>
      <div class={Style.departmentDiv}>
        <table>
          <thead>
            <tr>
              <th>Department Name</th>
              <th>Description</th>
              <th>Openings</th>
            </tr>
          </thead>
          {DepartmentJson.map((element, index) => (
            <tbody>
              <tr>
                <td>{element.Name}</td>
                <td>{element.Description}</td>
                <td>{element.departments[0].Openings.length}</td>
              </tr>
            </tbody>
          ))}
        </table>
      </div>
    </div >
  )
}
