import React, { useEffect, useState } from 'react';
import { AdminHeader } from '../AdminHeader/AdminHeader';
import Style from '../PanelistPage/PanelistPage.module.css';
import { Text } from '../Text/Text';
import data from './Panelist.json';
import { Heading } from '../Heading/Heading';
import { Button } from '../Button/Button';
import { TodayOpenings } from '../TodayOpenings/TodayOpening';

export const PanelistPage = () => {
    const [openings, setOpenings] = useState(data);
    const [openingView, setOpeningView] = useState(false);
    const [applicantView, setApplicantView] = useState(false);
    const [panelistDetails, setpanelistDetails] = useState({
        panelistName: '',
        panelistEmail: '',
        panelistPositionpanelistGender: '',
        panelistPosition: ''

    });
    // const [panelistPosition, setPanelistPosition] = useState('');
    // const [panelistEmail, setPanelistEmail] = useState('');

    // const fetchData = () => {
    //     fetch('http://localhost:8080/JobVista/GetCurrentOpenings', {
    //         method: 'GET',
    //         headers: {
    //             'Content-type': 'application/json'
    //         }
    //     })
    //         .then((response) => {
    //             if (!response.ok) {
    //                 throw new Error(`HTTP error status: ${response.status}`)
    //             }
    //             return response.json();
    //         })
    //         .then((result) => {
    //             console.log(result.message);
    //             setOpenings(result.message);
    //             console.log(openings);
    //         })
    //         .catch((error) => {
    //             console.log("Error in get openings ", error);
    //         })
    // }

    // useEffect(() => {
    //     fetchData();
    // }, [])

    // useEffect(() => {
    //     console.log(openings);
    // })

    useEffect(() => {
        if (data && data[0] && data[0].Status === 'Success') {
            setOpenings(data[0].Value);
        }
    }, [])

    const openingview = (name, position, email, gender) => {
        setOpeningView(true);
        panelistDetails.panelistName = name;
        panelistDetails.panelistEmail = email;
        panelistDetails.panelistGender = gender;
        panelistDetails.panelistPosition = position;
    }

    const viewApplicant = () => {
        setApplicantView(true);
        setOpeningView(false);
        console.log("Clicked Me");
        console.log("applicant: " + applicantView);
        console.log("applicant: " + applicantView);
    }

    const backBtn = () => {
        setOpeningView(false);
    }

    const currDate = new Date();
    const year = currDate.getFullYear();
    const month = currDate.getMonth() < 10 ? '0' + (currDate.getMonth() + 1) : currDate.getMonth() + 1;
    const date = currDate.getDate() < 10 ? '0' + (currDate.getDate()) : currDate.getDate();

    const res = `${year}-${month}-${date}`;

    return (
        <div id={Style.mainContainer} className={Style.commonStyle}>
            <AdminHeader />

            <div id={Style.mainSide} className={Style.commonStyle}>
                <div id={Style.leftSide} className={Style.commonStyle}>

                    <div id={Style.opening}>

                        {/* <Heading className={Style.title}>Today Openings</Heading> */}

                        <TodayOpenings />
                    </div>
                </div>

                {!openingView ?
                    <div id={Style.view}>
                        {openings.length === 0 ? (
                            <Text>No Openings Available</Text>
                        ) : (
                            <table>
                                <thead>
                                    <tr className={Style.trId}>
                                        <th className={Style.thId}>Opening</th>
                                        <th className={Style.thId}>Test</th>
                                        <th className={Style.thId}>Date</th>
                                        <th className={Style.thId}>Applicants</th>
                                    </tr>
                                </thead>
                                <tbody className={Style.tbodyId}>
                                    {openings.map((opening, openingIndex) => {
                                        if (opening.Opening && opening.Opening.startDate) {
                                            const startingDate = new Date(opening.Opening.startDate);
                                            if (startingDate >= currDate) {
                                                return (
                                                    <tr className={Style.trId} key={openingIndex}>
                                                        <td className={Style.tdId} onClick={() => openingview(opening.Panelist.panelistName, opening.Panelist.panelistPosition, opening.Panelist.panelistEmail, opening.Panelist.panelistGender)}>{opening.Opening.description}</td>
                                                        {opening.Tests && opening.Tests.length > 0 ? (
                                                            <React.Fragment>
                                                                {/* <div> */}
                                                                    <td className={Style.tdId} onClick={() => openingview(opening.Panelist.panelistName, opening.Panelist.panelistPosition, opening.Panelist.panelistEmail, opening.Panelist.panelistGender)}>{opening.Tests[0].testTitle}</td>
                                                                    <td className={Style.tdId} onClick={() => openingview(opening.Panelist.panelistName, opening.Panelist.panelistPosition, opening.Panelist.panelistEmail, opening.Panelist.panelistGender)}>{opening.Tests[0].testDate}</td>
                                                                {/* </div> */}
                                                                    <td className={Style.tdId}><Button id={Style.buttonId} onClick={viewApplicant}>View Applicants</Button></td>
                                                        </React.Fragment>
                                                        ) : (
                                                            <React.Fragment>
                                                            <td>-</td>
                                                            <td>-</td>
                                                            <td>-</td>
                                                        </React.Fragment>
                                                        )}
                                                    </tr>
                                                )
                                            } else {
                                                return null;
                                            }
                                        } else {
                                            return null;
                                        }
                                    })}
                                </tbody>
                            </table>
                        )}


                        <div id={Style.analytics} className={Style.common}>
                            <Heading className={Style.title}>Analytics View</Heading>
                        </div>
                    </div>
                    :
                    <div id={Style.rightSide} className={Style.commonStyle}>
                        <div id={Style.todayOpenings} className={Style.common}>
                            <Button onClick={backBtn} id={Style.backBtn}><i class="fa-solid fa-chevron-left"></i></Button>
                            <table>
                                <tr>
                                    <th>Panelist Name</th>
                                    <th>Panelist Position</th>
                                    <th>Panelist Email</th>
                                    <th>Panelist Gender</th>
                                </tr>

                                <tr>
                                    <td>{panelistDetails.panelistName}</td>
                                    <td>{panelistDetails.panelistPosition}</td>
                                    <td>{panelistDetails.panelistEmail}</td>
                                    <td>{panelistDetails.panelistGender}</td>
                                </tr>
                            </table>
                        </div>

                    </div>}
            </div>
        </div>
    )
}
