import React from 'react';
import Style from '../Popup/Popup.module.css';
import { Text } from '../Text/Text';

export const Popup = ({ isSuccess, successContent, failedContent }) => {

    const style = {
        background: isSuccess ? "#4188FF" : "red"
    }

    return (
        <div id={Style.popupContainer}>
            <div id={Style.iconDiv}><i class={isSuccess ? "fa-solid fa-check" : "fa-solid fa-exclamation"}></i></div>
            <Text id={Style.contentDiv}>{isSuccess ? successContent : failedContent}</Text>
        </div>
    )
}
