import React, { useState, useEffect } from "react";
import data from './Openings.json';
import { Text } from "../Text/Text";
import Style from '../TodayOpenings/TodayOpenings.module.css';
import { Image } from "../ImageTag/Image";
import { Heading } from "../Heading/Heading";

export const TodayOpenings = () => {

    const [openingsJson, setOpeningsJson] = useState([]);
    const [selectOpening, setSelectOpening] = useState(null);
    const [profile, setProfile] = useState(false);
    const [applicant, setApplicant] = useState([]);
    const [selectOption, setSelectOption] = useState(null);

    const currentDate = new Date();
    const currYear = currentDate.getFullYear();
    const currMonth = currentDate.getMonth() < 10 ? '0' + (currentDate.getMonth() + 1) : (currentDate.getMonth() + 1);
    const currDate = currentDate.getDate() < 10 ? '0' + (currentDate.getDate()) : currentDate.getDate();
    const formatDate = `${currYear}-${currMonth}-${currDate}`;

    const viewProfile = (applicant) => {
        setProfile(true);
        setApplicant(applicant);
    }

    const handleChange = (event) => {
        setSelectOpening(event.target.value);
    }

    const selectResult = (event) => {
        setSelectOption(event.target.value);
    }

    useEffect(() => {
        const defaultValue = openingsJson.findIndex(element => (
            element.test.some(test => test.Date === formatDate)
        ))
        setSelectOpening(defaultValue)
    }, [formatDate, openingsJson])


    const fetchData = () => {
            fetch('http://localhost:8080/JobVista/PanelistOpeningServlet', {
                method: 'GET',
                headers: {
                    'Content-type': 'application/json'
                }
            })
                .then((response) => {
                    if (!response.ok) {
                        throw new Error(`HTTP error status: ${response.status}`)
                    }
                    return response.json();
                })
                .then((result) => {
                    console.log(result.Value.upComingOpenings);
                    setOpeningsJson(result.Value.upComingOpenings);
                    // console.log(openingsJson);
                })
                .catch((error) => {
                    console.log("Error in get openings ", error);
                })
        }

        useEffect(() => {
            fetchData();
        }, [])

        // useEffect = (() => {
        //     fetchCallForCurrentOpenings();
        // }, [])

    return (
        <div>
            <select id={Style.select} onChange={handleChange}>
                {openingsJson.map((element, index) => (
                    element.test.some(test => test.Date === formatDate) && (
                        <option key={index} value={index}>{element.title}</option>
                    )
                ))}
            </select>

            <div id={Style.level}>
                <i class="fa-solid fa-chevron-left"></i>

                <Text>Level 1</Text>

                {/* {openingsJson.map((element, index) => (
                    <Text>Level{element.test[index].roundOn}</Text>
                ))} */}

                <i class="fa-solid fa-chevron-right"></i>
            </div>

            {/* {selectOpening !== null && (
                <table>

                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Mobile Number</th>
                        <th>Points</th>
                        <th>Result</th>
                        <th></th>
                    </tr>

                    {openingsJson[selectOpening].test.map((test, index) => (
                        test.Date === formatDate && test.applicants.map((applicant, idx) => (
                            <tr key={idx} className={Style.hover}>
                                <td>{applicant.name}</td>
                                <td>{applicant.email}</td>
                                <td>{applicant.mobileNumber}</td>
                                <td>{applicant.points}</td>
                                <td>
                                    {!selectOption && (
                                        <select onChange={selectResult}>
                                            <option>Select</option>
                                            <option>Onhold</option>
                                            <option>Reject</option>
                                        </select>
                                    )}
                                    {console.log(selectOption)}
                                    {selectOption && (
                                        <p onClick={() => setSelectOption(false)} id={Style.selectOption}>{selectOption}</p>
                                    )}
                                </td>
                                <td className={Style.icon}><i onClick={() => viewProfile(applicant)} id={Style.icon} class="fa-solid fa-circle-info"></i></td>

                            </tr>
                        ))
                    ))}

                    {profile ?
                        <div id={Style.viewProfile}>
                            <i class="fa-solid fa-circle-xmark" onClick={() => setProfile(false)} id={Style.crossBtn}></i>

                            <Image id={Style.img} src='../../Images/user.png' />

                            <Heading id={Style.name}>{applicant.name} - <Text id={Style.email}>{applicant.email}</Text></Heading>

                            <Text className={Style.fntSize}>Contact - {applicant.mobileNumber}</Text>

                            <ul>
                                <li className={Style.fontSize}>Social Media Resources: </li>
                                {Object.entries(applicant.socialMediaResorse).map(([key, value]) => (
                                    <li className={Style.fntSize} key={key}>{key}: {value}</li>
                                ))}
                            </ul>
                        </div>
                        :
                        ''
                    }
                </table>
            )} */}
        </div>
    )

}

