import React, { useEffect, useState } from 'react';
import Style from '../Departments/Departments.module.css';
import { Heading } from '../Heading/Heading';
import { Button } from '../Button/Button';

export const Departments = () => {
  const [departmentJson, setDepartmentJson] = useState([]);
  const [selectedDepartmentIndex, setSelectedDepartmentIndex] = useState(null);
  const [selectedOpeningIndex, setSelectedOpeningIndex] = useState(null);
  const [view, setView] = useState(false);
  const [applicantView, setApplicantView] = useState(false);

  const viewOpeningDetails = (departmentIndex) => {
    setView(true);
    setSelectedDepartmentIndex(departmentIndex);
  }

  const viewApplicantDetails = (openingIndex) => {
    setSelectedOpeningIndex(openingIndex);
  }

  const backBtn = () => {
    setView(false);
  }

  useEffect(() => {
    fetchCallForDepartments();
  }, []);

  const fetchCallForDepartments = () => {
    fetch("http://localhost:8080/JobVista/AdminDashBoard", {
      method: 'GET',
      headers: {
        'Content-type': 'Application/JSON'
      }
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(result => {
        console.log("Fetched data:", result);
        console.log("Departments:", result.message);
        setDepartmentJson(result.message.departments);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }

  const handleClick = (id) => {
    console.log("Clicked");
    console.log(id);
    setApplicantView(true);
  }

  return (
    <div>

      <Heading>Departments</Heading>
      {!view ? (
        <table>
        <thead>
          <tr className={Style.openingTr}>
            <th>Department Name</th>
            <th>Description</th>
            <th>Openings</th>
          </tr>
        </thead>
        <tbody>
          {departmentJson.map((department, index) => (
            <tr key={index} onClick={() => viewOpeningDetails(index)}>
              <td className={Style.openingTd}>{department.Title}</td>
              <td className={Style.openingTd}>{department.Description}</td>
              <td className={Style.openingTd}>{department.openings.length}</td>
            </tr>
          ))}
        </tbody>
      </table>

      ) : 
          (
            !applicantView ? (
              <div>
              {selectedDepartmentIndex !== null && (
        <div>
          <Heading>{departmentJson[selectedDepartmentIndex].Name} Openings</Heading>
          <Button onClick={backBtn}>Back</Button>
          <table>
            <thead>
              <tr className={Style.openingTr}>
                <th>Opening Name</th>
                <th>Experience</th>
                <th>Qualification</th>
                <th>Employment Type</th>
                <th>Salary Range</th>
                <th>Applicants</th>
                <th>Start_Date</th>
                <th>End_Date</th>
              </tr>
            </thead>
            <tbody>
              {departmentJson[selectedDepartmentIndex].openings.map((openings, index) => (
                <tr key={index} onClick={() => handleClick(index)}>
                  <td>{openings.Description}</td>
                  <td>{openings.Experience} years</td>
                  <td>{openings.Qualification}</td>
                  <td>{openings.EmploymentType}</td>
                  <td>{openings.SalaryRange}</td>
                  <td>{openings.applicants.length}</td>
                  <td>{openings.Start_Date}</td>
                  <td>{openings.End_Date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
            </div>
            ):(
              <div>
              {selectedDepartmentIndex !== null && (
        <div>
          <Heading>{departmentJson[selectedDepartmentIndex].Name} Openings</Heading>
          <Button onClick={backBtn}>Back</Button>
          <table>
            <thead>
            <tr className={Style.openingTr}>
            <th>Name</th>
            <th>Gender</th>
            <th>Email</th>
            <th>DOB</th>
            <th>Phone</th>
            <th>Qualification</th>
            <th>Experience</th>
          </tr>
            </thead>
            <tbody>
            {departmentJson[selectedDepartmentIndex].openings.applicants.map((openings, index) => (
                <tr key={index} onClick={() => handleClick(index)}>
                  <td>{openings.Description}</td>
                  <td>{openings.Experience} years</td>
                  <td>{openings.Qualification}</td>
                  <td>{openings.EmploymentType}</td>
                  <td>{openings.SalaryRange}</td>
                  <td>{openings.applicants.length}</td>
                  <td>{openings.Start_Date}</td>
                  <td>{openings.End_Date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
            </div>
            )

          )
      }
    </div>
  );
};
