import React, { useState } from "react";
import { Heading } from "../Heading/Heading";

export const TotalHire = () => {


    const [hiredPercentage, setHiredPercentage] = useState([]);


    const fetchCallForPercentage = () => {
        fetch("http://localhost:8080/JobVista/Graphs", {
          method: 'POST',
          headers: {
            'Content-type': 'application/JSON'
          }
        })
          .then(response => {
            if (!response.ok) {
              throw new Error(`HTTP error! Status: ${response.status}`);
            }
            return response.json();
          })
          .then(result => {
            console.log("Fetched data:", result);
            console.log("Hired:", result.message);
            setHiredPercentage(result.differenceInMonthByHired);
          })
          .catch(error => {
            console.error('Error fetching data:', error);
          });
      }

      useEffect(() => {
        fetchCallForPercentage();
    }, []);

    return(
        <div>
            <Heading>{hiredPercentage}</Heading>
        </div>
    )

}
