import React from 'react';
import { Text } from '../Text/Text';
import Style from '../AdminBox/AdminBox.module.css';
import { Heading } from '../Heading/Heading';

const AdminBox = ({ hireCount, percentageChange, title }) => {
    const arrowIcon = percentageChange.includes('-') ? 'fa-arrow-down' : 'fa-arrow-up';

    const style = {
        color: percentageChange.includes('-') ? '#E43636' : '#62D8AE'
    }

    return (
        <div id={Style.boxContainer}>
            <div id={Style.icon}><i className={`fa-solid ${arrowIcon}`}></i></div>
            <div id={Style.content}>
                <Text id={Style.title}>{title}</Text>
                <div id={Style.data}>
                    <Heading>{hireCount}</Heading>
                    <span id={Style.percent} style={style}>{percentageChange}</span>
                </div>
            </div>
        </div>
    );
};

export default AdminBox;
