import React, {useEffect, useState } from 'react';
import { Heading } from '../Heading/Heading';
import { Button } from '../Button/Button';
import { Text } from '../Text/Text';
import Style from '../Panelist/Panelist.module.css';
import data from './data.json';
import { Input } from '../Input/Input';
import { useRef } from 'react';

export const Panelist = () => {



  const [panelistJson, setPanelistJson] = useState([]);

    const fetchCallPanelist = () => {
      fetch("http://localhost:8080/JobVista/GetPanelist", {
        method: 'GET',
        headers: {
          'Content-type': 'Application/JSON'
        }
      })
        .then(response => {
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          return response.json();
        })
        .then(result => {
          console.log("panelist: " + result.message);
          setPanelistJson(result.message);
        })
        .catch(error => {
          console.error('Error fetching data:', error);
        });
    }

    useEffect(() => {
      fetchCallPanelist();
    }, []);


  const [panelist, setPanelist] = useState(data);
  const [view, setView] = useState(false);

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [gender, setGender] = useState('');
  const [department, setDepartment] = useState('');
  const [position, setPosition] = useState('');
  const [password, setPassword] = useState('');
  const getValue = useRef();
  const [departments, setDepartments] = useState([]);

  var userObj = JSON.parse(localStorage.getItem('userDetails'));

    const fetchCallForDepartment = () => {
            fetch('http://localhost:8080/JobVista/GetDepartments',{
            method:'POST',
            headers:{
                'Content-type':'application/json'
            },
            body: JSON.stringify({'userDetails':userObj})

        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch departments');
                }
                return response.json();
            })
            .then(data => {
                const departmentsName = data.message.map(department => department.Title);
                setDepartments(departmentsName);
            })
            .catch(error => {
                console.log('Error fetching departments:', error);
            });
        }

    useEffect (() => {
        fetchCallForDepartment();
      }, []);
      useEffect (() => {
        console.log(departments);
      }, [departments]);


  const handleAddPanelist = () => {
    setView(true);
  };

  const backBtn = () => {
    setView(false);
  }

  const addPanelist = () => {

    const userDetails = JSON.parse(localStorage.getItem('userDetails'));

    if (!userDetails) {
        console.error('User details not found in local storage.');
        return;
    }

    fetch("http://localhost:8080/JobVista/AddPanelist", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        panelist:{
        name,
        email,
        gender,
        position,
        password,
        department
        },
        userDetails:userDetails
      }),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Failed to add panelist');
      }
      return response.json();
    })
    .then(data => {
      console.log('Panelist added successfully:', data);
      setPanelist([...panelist, data]);
      setName('');
      setEmail('');
      setGender('');
      setDepartment('');
      setPosition('');
      setPassword('');
      setView(false);
    })
    .catch(error => {
      console.error('Error adding panelist:', error);
    });
  }

  return (
    <div className={Style.commonStyle}>
      {view ? (
        <div id={Style.addPanelist}>
           <Heading className={Style.title}><i class="fa-regular fa-circle-left" onClick={backBtn}></i> Add Panelist</Heading>
          <div className={`${Style.perfectDiv} ${Style.flx}`}>
            <ul className={`${Style.list-1} ${Style.listing} ${Style.inlineBlock} ${Style.ulWidth1}`}>
              <li>Name</li>
              <li>Email</li>
              <li>Password</li>
              <li>Gender</li>
              <li>Department</li>
              <li>Position</li>
            </ul>
            <ul className={`${Style.list-1} ${Style.listing} ${Style.inlineBlock} ${Style.ulWidth2}`}>
              <li><Input type="text" value={name} onChange={(e) => setName(e.target.value)} /></li>
              <li><Input type="email" value={email} onChange={(e) => setEmail(e.target.value)} /></li>
              <li><Input type="password" value={password} onChange={(e) => setPassword(e.target.value)} /></li>
              <li>
                <label>Male</label>
                <Input ref={getValue} type="radio" name="gender" checked={gender === 'Male'} onChange={() => setGender('Male')} />
                <label>Female</label>
                <Input ref={getValue} type="radio" name="gender" checked={gender === 'Female'} onChange={() => setGender('Female')} />
                <label>NB</label>
                <Input ref={getValue} type="radio" name="gender" checked={gender === 'NB'} onChange={() => setGender('Non-Binary')} />
              </li>
              <li> 
                <Input list='departmentsName' name={department} onChange={(e) => setDepartment(e.target.value)}/>
                <datalist id='departmentsName'>
                  {departments.map((dept, index) => (
                    <option key={index} value={dept}>{dept}</option>
                  ))}
                  </datalist>
              </li>
              <li><Input type="text" value={position} onChange={(e) => setPosition(e.target.value)} /></li>
          </ul>
        </div>
        <div className={`${Style.flx} ${Style.CancelOrSubmit}`}>
          <Button id={Style.CancelAddPanelist} onClick={backBtn}>Cancel</Button>
          <Button onClick={addPanelist}>Submit</Button>
        </div>
      </div>
      ) :
      <div>
        <div className={Style.flx} id={Style.header}>
        <Heading>Panelist</Heading>
        <Button id={Style.panelistBtn} onClick={handleAddPanelist}>Add Panelist</Button>
      </div>

      <ul id={Style.panelist} className={Style.flx}>
        <div id={Style.li}>
          <li>Name</li>
          <li>Email</li>
          <li>Gender</li>
          <li>Department</li>
          <li>Organization</li>
          <li>Roles</li>
        </div>
      </ul>

      {panelistJson.length === 0 ? (
        <div></div>
      ) : (
        <div id={Style.main}>
          {panelistJson.map((panelistValue, index) => (
            <ul className={Style.flx} key={index} id={Style.lists}>
              <div className={Style.common} id={Style.li}>
                <li>{panelistValue.Name}</li>
                <li>{panelistValue.Email}</li>
                <li>{panelistValue.Gender}</li>
                <li>{panelistValue.Title}</li>
                <li>{panelistValue.Position}</li>
              </div>
            </ul>
          ))}
        </div>
      )}

      </div>}
    </div>
  )
}












