import React, { useState, useEffect } from "react";
import data from './Openings.json';
import { Text } from "../Text/Text";
import Style from '../TodayOpenings/TodayOpenings.module.css';
import { Image } from "../ImageTag/Image";
import { Heading } from "../Heading/Heading";

export const TodayOpenings = () => {

    const [openingsJson, setOpeningsJson] = useState([]);
    const [selectOpening, setSelectOpening] = useState(-1);
    const [profile, setProfile] = useState(false);
    const [applicant, setApplicant] = useState([]);
    const [selectOption, setSelectOption] = useState(null);
    const [currentRound, setCurrentRound] = useState(1);
    const [maxRound, setMaxRound] = useState(1);
    const [currApplicant, setCurrentApplicant] = useState({
        "testId": '',
        "applicantId": '',
        "points": '',
        "status": ''
    });

    const [sampleName, setSampleName] = useState([]);

    const currentDate = new Date("2024-03-05");
    const currYear = currentDate.getFullYear();
    const currMonth = currentDate.getMonth() < 10 ? '0' + (currentDate.getMonth() + 1) : (currentDate.getMonth() + 1);
    const currDate = currentDate.getDate() < 10 ? '0' + (currentDate.getDate()) : currentDate.getDate();
    const formatDate = `${currYear}-${currMonth}-${currDate}`;

    const viewProfile = (applicant) => {
        setProfile(true);
        setApplicant(applicant);
        console.log(applicant.Name);
    }

    const handleChange = (event) => {
        setSelectOpening(parseInt(event.target.value));
    }

    const selectResult = (event) => {
        setSelectOption(event.target.value, () => {
            console.log("option: ", selectOption);
        });
    }    

    const handlePointsChange = (fieldName, value, testId, applicantId, event, selectStatus) => {
        console.log("selectoption: " ,selectStatus);
        // setSelectOption(selectStatus);
        console.log("option: ", selectOption);
        setCurrentApplicant({
            testId: testId,
            applicantId: applicantId,
            points: value,
            status: selectOption
        })

        console.log(currApplicant);
    };

    useEffect(() => {
        const defaultValue = openingsJson.findIndex(element => (
            element.test.map((sampleTest, ind) => (
                sampleTest.testDate === formatDate
            )
            )
        ))


        setSelectOpening(defaultValue)
    }, [formatDate, openingsJson, selectOpening])

    const fetchData = () => {
        fetch('http://localhost:8080/JobVista/PanelistOpeningServlet', {
            method: 'GET',
            headers: {
                'Content-type': 'application/json'
            }
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error(`HTTP error status: ${response.status}`)
                }
                return response.json();
            })
            .then((result) => {
                setOpeningsJson(result.Value.upComingOpenings);


            })
            .catch((error) => {
                console.log("Error in get openings ", error);
            })
    }

    useEffect(() => {
        fetchData();
    }, [])

    const fetchCallForSettingPoints = () => {
        fetch("http://localhost:8080/JobVista/InterviewerSetMarksServlet", {
      method: 'POST',
      headers: {
        'Content-type': 'application/JSON'
      },
      body: JSON.stringify(currApplicant)
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(result => {
        console.log("Fetched data:", result);
        console.log("result:", result.message);
        if(result.status == "success"){
          alert(result.message);
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
    }

    // useEffect(() => {
    //     console.log("currApplicant has been updated:", currApplicant);
    // }, [currApplicant, selectOption]);

    useEffect(() => {
        if (selectOpening >= 0) {
            openingsJson[selectOpening].test.forEach((sampleTest) => {
                if (sampleTest.templateRoundOn >= maxRound && sampleTest.testDate === formatDate) {
                    setMaxRound(sampleTest.templateRoundOn);
                }
            });


        }
    }, [selectOpening, openingsJson, maxRound]);

    let style;

    const handleNextRound = () => {
        if (currentRound + 1 <= maxRound) {
            setCurrentRound(currentRound + 1);
            console.log(currentRound, " ", maxRound);
            style = {
                color: 'gray'
            }
        }
    };

    const handlePreviousRound = () => {
        if (currentRound > 1) {
            setCurrentRound(currentRound - 1);

            style = {
                color: 'gray'
            }
        }
    };

    const handleInput = (event) => {
        event.target.textContent = event.target.textContent.replace(/\D/g, '');
    }

    const handleStatus = (e) => {
        console.log(e.tar);
    }

    return (
        <div>
            <select id={Style.select} onChange={handleChange}>
                {openingsJson.length > 0 && openingsJson.map((opening, index) => (
                    opening.test.map((sampleTest, ind) => (
                        sampleTest.testDate === formatDate && (

                            <React.Fragment key={ind}>

                                <option value={ind}>{sampleTest.testTitle}</option>
                                <option value={ind}>{sampleTest.templateRoundOn}</option>
                            </React.Fragment>
                        )
                    ))
                ))}
            </select>

            <div id={Style.level}>
                <Text>
                    <i id={style} className="fa-solid fa-chevron-left" onClick={handlePreviousRound} disabled={currentRound === 1}></i>
                    Round {currentRound}
                    <i id={style} className="fa-solid fa-chevron-right" onClick={handleNextRound}></i>
                </Text>
            </div>

            {selectOpening !== null && (
                <table>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Points</th>
                        <th>Results</th>
                        <th>Info</th>
                    </tr>
                    {selectOpening >= 0 && openingsJson[selectOpening].test.map((sampleTest, ind) => {

                        if (sampleTest.templateRoundOn === currentRound && sampleTest.applicants && sampleTest.applicants[0] && sampleTest.testDate === formatDate) {


                            return sampleTest.applicants[0].map((sample, idx) => (
                                <tr key={idx} className={Style.hover}>
                                    <td>{sample.Name}</td>
                                    <td>{sample.Email}</td>
                                    <td>{sample.Phone}</td>
                                    <td contentEditable="true" onInput={(e) => {handleInput(e); handlePointsChange('points', e.target.textContent, sampleTest.testId, sample.Applicant_Id);}}>{sample.Points}</td>
                                    <td>
                                        {/* <select onChange={(e) => setSelectOption(e.target.value)}>
                                            <option value='Select'>Select</option>
                                            <option value='Onhold'>Onhold</option>
                                            <option value='Reject'>Reject</option>
                                        </select> */}

                                        <select onClick={handleStatus}>
                                            <option value='Select'>Select</option>
                                            <option value='Onhold'>Onhold</option>
                                            <option value='Reject'>Reject</option>
                                        </select>
                                    </td>
                                    <button id={Style.but} onClick={fetchCallForSettingPoints}></button>
                                    <td className={Style.icon}><i onClick={() => viewProfile(sample)} id={Style.icon} className="fa-solid fa-circle-info"></i></td>
                                </tr>
                            ));
                        }
                        return null;
                    })}
                </table>
            )}

            {profile && (
                <div id={Style.viewProfile}>
                    <i className="fa-solid fa-circle-xmark" onClick={() => setProfile(false)} id={Style.crossBtn}></i>
                    <Image id={Style.img} src='../../Images/user.png' />

                    <Heading id={Style.name}>{applicant.Name} - <Text id={Style.email}>{applicant.Email}</Text></Heading>
                    <Text className={Style.fntSize}>Contact - {applicant.Phone}</Text>
                    {/* <ul>
                        <li className={Style.fontSize}>Social Media Resources: </li>
                        {Object.entries(applicant.socialMediaResorse).map(([key, value]) => (
                            <li className={Style.fntSize} key={key}>{key}: {value}</li>
                        ))}
                    </ul> */}
                </div>
            )}
        </div>
    );

}
