import React, { useState } from 'react';
import data from './data.json';
import { Heading } from '../Heading/Heading';
import { Text } from '../Text/Text';
import Style from '../Departments/Departments.module.css';

export const Departments = () => {
  const [DepartmentJson, getDepartmentJson] = useState(data[0].departments);
  const [selectedDepartment, setSelectedDepartment] = useState(data[0].departments[0]); 
  const [selectedOpening, setSelectedOpening] = useState(selectedDepartment.Openings[0]); 

  const viewOpeningDetails = (departmentIndex) => {
    // setSelectedDepartment(DepartmentJson[departmentIndex]);
    setSelectedOpening(data[departmentIndex].departments.Openings[0].Description);
  }
console.log(viewOpeningDetails);
  return (
    <div class={Style.departmentDiv}>
      <Heading>Departments</Heading>
      <div class={Style.departmentDiv}>
        <table>
          <thead>
            <tr>
              <th>Department Name</th>
              <th>Description</th>
              <th>Openings</th>
            </tr>
          </thead>
          <tbody>
            {DepartmentJson.map((department, index) => {
              console.log(`Department Name: ${department.Name}`);
              console.log(`Department Description: ${department.Description}`);
              // console.log(`openings Description: ${department.Openings[0].Description}`);
              // console.log(`Department opening: ${department.Openings.length}`);
              return (
                <tr key={index} onClick={() => console.log("opening desc: " + viewOpeningDetails(index, 0) + " " + index)}>
                  <td>{department.Name}</td>
                  <td>{department.Description}</td>
                  <td>{department.Openings.length}</td>
                  <td>{department.Openings[0].EmploymentType}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>



    </div>
  );
};
