          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          return response.json();
        })
        .then(result => {
          console.log("Fetched data:", result);
          setLabelData(result.selectedApplicantsGraphInDepartments);
          console.log("labelData:", labelData); // Add this line
        })
        .catch(error => {
          console.error('Error fetching data:', error);
        });
      }, []);

      const labelss = labelData.map(item => Object.keys(item)[0]);
      const datasets = labelData.map(item => item[Object.keys(item)[0]]);
      console.log("labels:", labelss); // Add this line
      console.log("datas:", datass); // Add this line


  return (
    <div>
      <Pie data={data}/> 
    </div>
  );
};

export default LineChart;
