import React, { useEffect, useState } from 'react';
import Style from '../Openings/Openings.module.css';
import { Heading } from '../Heading/Heading';
import { Button } from '../Button/Button';
import Chart from '../BarChart/Chart';

export const Openings = () => {
  const [openingsJson, setOpeningsJson] = useState([]);
  const [openingIndex, setSelectedOpeningIndex] = useState(null);
  const [view, setView] = useState(false);

  const viewDetails = (openingIndex) => {
    setView(true);
    setSelectedOpeningIndex(openingIndex);
  }

  const backBtn = () => {
    setView(false);
  }

  useEffect(() => {
    fetchCallForOpenings();
  }, []);

  const fetchCallForOpenings = () => {
    fetch("http://localhost:8080/JobVista/GetOpenings", {
      method: 'GET',
      headers: {
        'Content-type': 'Application/JSON'
      }
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(result => {
        console.log("Fetched data:", result);
        console.log("Departments:", result.Value);
        setOpeningsJson(result.Value);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }

  useEffect(() => {
    console.log("json: " + openingsJson);
  }, []);

  return (
    <div>


        <div id={Style.applicants}>
        <Heading>Openings</Heading>
        {/* <Chart /> */}

        <table>
          <thead>
            <tr className={Style.openingTr}>
              <th>Opening Name</th>
              <th>Qualification</th>
              <th>Experience</th>
              <th>Employment Type</th>
              <th>Salary Range</th>
              <th>Start_Date</th>
              <th>End_Date</th>
              <th>Department Name</th>
            </tr>
          </thead>
          <tbody>
          {openingsJson.map((item, index) => (
           Object.values(item.Opening).map(openingValue => {
              <tr key={index}>

                <td>{openingValue.description} description</td>
                <td>{openingValue.qualification}</td>
                <td>{openingValue.experience}</td>
                <td>{openingValue.employmentType}</td>
                <td>{openingValue.salaryRange}</td>
                <td>{openingValue.startDate}</td>
                <td>{openingValue.endDate}</td>
                <td>{openingValue.description}</td>
              </tr>
            })
          ))}
          </tbody>

        </table>
        </div>

      </div>
  )
}
