import React, { useState, useEffect } from "react";
import { Text } from "../Text/Text";


const PanelistTextPrint = () => {
    const [totalOpeningsJson, setTotalOpeningsJson] = useState({
        "totalOpenings": '',
        "totalApplicants": '',
        "selectedApplicants": ''
    });
    // const [totalApplicants, setTotalApplicants] = useState(null);
    // const [selectedApplicants, setSelectedApplicants] = useState(null);

    // const result = {"totalOpenings":6,"totalApplicants":22,"selectedApplicants":8};

    // totalOpeningsJson.totalOpenings = result.totalOpenings;
    // totalOpeningsJson.totalApplicants = result.totalApplicants;
    // totalOpeningsJson.selectedApplicants = result.selectedApplicants;
        // setTotalApplicants(result.totalApplicants);
        // setSelectedApplicants(result.selectedApplicants);

    const fetchData = () => {
        fetch("http://localhost:8080/JobVista/Graph", {
            method: 'POST',
            headers: {
                'Content-type': 'Application/JSON'
            },
            body: JSON.stringify({ 'userDetails': JSON.parse(localStorage.getItem('userDetails')) })
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then(result => {
                console.log("chart: ", result);
                totalOpeningsJson.totalOpenings = result.departmentDetails.totalOpenings;
                totalOpeningsJson.totalApplicants = result.departmentDetails.totalApplicants;
                totalOpeningsJson.selectedApplicants = result.departmentDetails.selectedApplicants;

            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }

    useEffect(() => {
        fetchData();
    }, []);

    return (
        <div>
            <Text>Total Openings :  {totalOpeningsJson.totalOpenings}</Text>
            <Text>Total Applicants :  {totalOpeningsJson.totalApplicants}</Text>
            <Text>Selected Applicants :  {totalOpeningsJson.selectedApplicants}</Text>
        </div>

    );
};

export default PanelistTextPrint;
