import React, { useState, useEffect } from "react";
import Chart from "chart.js/auto";
import { Pie } from "react-chartjs-2";

const LineChart = () => {
    const [labelData, setLabelData] = useState([]);
    const [label, setLabel] = useState([]);
    const [datas, setDatas] = useState([]);
    const data = {
        labels: label,
        datasets: [
            {
                label: "My First dataset",
                backgroundColor: "rgb(255, 99, 132)",
                borderColor: "rgb(255, 240, 132)",
                data: datas,
            },
        ],
    };

    useEffect(() => {
        first()
    }, []);

    function first() {
        fetch("http://localhost:8080/JobVista/Graphs", {
            method: 'GET',
            headers: {
                'Content-type': 'Application/JSON'
            }
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then(result => {
                console.log("Fetched data:", result);
                console.log(result.selectedApplicantsGraphInDepartments);
                setLabelData(result.selectedApplicantsGraphInDepartments);
            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }
    useEffect(() => {
        second();
    },[labelData])

    function second() {
        if (labelData.length > 0) {
            labelData.map((item, index) => (
                setLabel(prevArray => [...prevArray, Object.keys(item)])
            ))
            labelData.map((item, index) => (
                setDatas(prevArray => [...prevArray, Object.values(item)])
            ))
            // setDatas(keys.map(key => labelData[0][key]))
        }

    }

    return (
        <div>
            <Pie data={data} />
        </div>
    );
};

export default LineChart;
