import React, { useState, useEffect } from "react"; 
import Chart from "chart.js/auto"; 
import { Pie } from "react-chartjs-2"; 

const LineChart = () => {
    const [labelData, setLabelData] = useState([]);
    const [label, setLabel] = useState([]);
    const [datas, setDatas] = useState([]);   
    const data = {
        labels: label,
        datasets: [
          {
            label: "My First dataset", 
            backgroundColor: "rgb(255, 99, 132)", 
            borderColor: "rgb(255, 240, 132)", 
            data: datas, 
          },
        ],
      };

      useEffect(() => {
        fetch("http://localhost:8080/JobVista/Graphs", {
          method: 'GET',
          headers: {
            'Content-type': 'Application/JSON'
          }
        })
          .then(response => {
            if (!response.ok) {
              throw new Error(`HTTP error! Status: ${response.status}`);
            }
            return response.json();
          })
          .then(result => {
            console.log("Fetched data:", result);
            setLabelData(result.selectedApplicantsGraphInDepartments);
            console.log("labelData: " + labelData);
          })
          .catch(error => {
            console.error('Error fetching data:', error);
          });
      }, []);


        // console.log(label);
        // setDatas(Object.keys(label));
        // console.log(datas);

        // {labelData.map(((Object.keys(label)), index) => (
        //     setLabel(prevArray => [...prevArray, item])
        // ))}

  return (
    <div>
      <Pie data={data}>Pie</Pie> 
    </div>
  );
};

export default LineChart;
