import React, { useEffect, useState } from 'react';
import Style from '../Openings/Openings.module.css';
import { Heading } from '../Heading/Heading';
import { Button } from '../Button/Button';
import Chart from '../BarChart/Chart';

export const Openings = () => {
  const [openingsJson, setOpeningsJson] = useState([]);
  const [openingIndex, setSelectedOpeningIndex] = useState(null);
  const [view, setView] = useState(false);

  const viewDetails = (openingIndex) => {
    setView(true);
    setSelectedOpeningIndex(openingIndex);
  }

  const backBtn = () => {
    setView(false);
  }

  useEffect(() => {
    fetchCallForDepartments();
  }, []);

  const fetchCallForDepartments = () => {
    fetch("http://localhost:8080/JobVista/GetOpenings", {
      method: 'GET',
      headers: {
        'Content-type': 'Application/JSON'
      }
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(result => {
        console.log("Fetched data:", result);
        console.log("Departments:", result.value);
        setOpeningsJson(result.Value[0].Opening);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }

  return (
    <div>


        <div id={Style.applicants}>
        <Heading>Openings</Heading>
        {/* <Chart /> */}
      <table>
          <thead>
            <tr className={Style.openingTr}>
              <th>Opening Name</th>
              <th>Qualification</th>
              <th>Employment Type</th>
              <th>Salary Range</th>
              <th>Applicants</th>
              <th>Start_Date</th>
              <th>End_Date</th>
            </tr>
          </thead>
          <tbody>

              <tr>
                <td>{openingsJson.Description}</td>
                <td>{openingsJson.Qualification}</td>
                <td>{openingsJson.EmploymentType}</td>
                <td>{openingsJson.SalaryRange}</td>
                <td>{openingsJson.applicants.length}</td>
                <td>{openingsJson.Start_Date}</td>
                <td>{openingsJson.End_Date}</td>
              </tr>
          </tbody>
        </table>
        </div>

      </div>
  )
}
