import React, { useState } from 'react';
import data from './data.json';
import Style from '../Departments/Departments.module.css';

export const Departments = () => {
  const [departmentJson] = useState(data[0].departments);
  const [selectedDepartmentIndex, setSelectedDepartmentIndex] = useState(null);

  const viewOpeningDetails = (departmentIndex) => {
    setSelectedDepartmentIndex(departmentIndex);
  }

  return (
    <div>
      <h1>Departments</h1>
      <table>
        <thead>
          <tr>
            <th>Department Name</th>
            <th>Description</th>
            <th>Openings</th>
          </tr>
        </thead>
        <tbody>
          {departmentJson.map((department, index) => (
            <tr key={index} onClick={() => viewOpeningDetails(index)}>
              <td>{department.Name}</td>
              <td>{department.Description}</td>
              <td>{department.Openings.length}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedDepartmentIndex !== null && (
        <div>
          <h2>{departmentJson[selectedDepartmentIndex].Name} Openings</h2>
          {departmentJson[selectedDepartmentIndex].Openings.map((opening, index) => (
            <div key={index}>
              <h3>{opening.Description}</h3>
              <p>Experience: {opening.Experience} years</p>
              <p>Qualification: {opening.Qualification}</p>
              <p>Employment Type: {opening.EmploymentType}</p>
              <p>Salary Range: {opening.SalaryRange}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
