import React, { useEffect, useState } from 'react';
import Style from '../Openings/Openings.module.css';
import { Heading } from '../Heading/Heading';
import { Button } from '../Button/Button';

export const Openings = () => {
  const [openingsJson, setopeningsJson] = useState([]);

  const viewDetails = (departmentIndex) => {
    setView(true);
    setSelectedOpeningIndex(departmentIndex);
  }

  useEffect(() => {
    fetchCallForDepartments();
  }, []);

  const fetchCallForDepartments = () => {
    fetch("http://localhost:8080/JobVista/GetOpenings", {
      method: 'GET',
      headers: {
        'Content-type': 'Application/JSON'
      }
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(result => {
        console.log("Fetched data:", result);
        console.log("Departments:", result.message);
        setopeningsJson(result.message);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }

  return (
    <div>
      <div>Openings</div>
      <table>
          <thead>
            <tr>
              <th>Department Name</th>
              <th>Description</th>
              <th>Openings</th>
            </tr>
          </thead>
          <tbody>
            {openingsJson.map((openings, index) => (
              <tr key={index} onClick={() => viewDetails(index)}>
                <td>{openings.Title}</td>
                <td>{openings.Description}</td>
                <td>{openings.length}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
  )
}
