import React, { useState, useEffect } from "react";
import { Pie } from "react-chartjs-2";

const LineChart = () => {
  const [labelData, setLabelData] = useState([]);
  const [label, setLabel] = useState([]);
  const [datas, setDatas] = useState([]);   
  const data = {
    labels: label,
    datasets: [
      {
        label: "My First dataset", 
        backgroundColor: "rgb(255, 99, 132)", 
        borderColor: "rgb(255, 240, 132)", 
        data: datas, 
      },
    ],
  };

  useEffect(() => {
    fetch("http://localhost:8080/JobVista/Graphs", {
      method: 'GET',
      headers: {
        'Content-type': 'Application/JSON'
      }
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      return response.json();
    })
    .then(result => {
      console.log("Fetched data:", result);
      const keys = Object.keys(result.selectedApplicantsGraphInDepartments[0]);
      setLabel(keys);
      setDatas(keys.map(key => result.selectedApplicantsGraphInDepartments[0][key]));
      console.log("labelData: " + labelData);
    })
    .catch(error => {
      console.error('Error fetching data:', error);
    });
  }, []);

  return (
    <div>
      <Pie data={data}/> 
    </div>
  );
};

export default LineChart;
