import React, { useState, useEffect } from "react";
import Chart from "chart.js/auto";
import { Pie } from "react-chartjs-2";



// box-sizing: border-box;
// display: block;
// height: 318px;
// width: 754px;


const LineChart = () => {
    const [label, setLabel] = useState([]);
    const [datavalue, setdatavalue] = useState([]);
    const styles = {
        boxSizing: 'border-box',
        display: 'block',
        height: '318px',
        width: '754px',
      };
    const fetchData = () => {
        fetch("http://localhost:8080/JobVista/Graphs", {
            method: 'GET',
            headers: {
                'Content-type': 'Application/JSON'
            }
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then(result => {
                const selectedApplicantsGraphInDepartments = result.selectedApplicantsGraphInDepartments;
                console.log(selectedApplicantsGraphInDepartments);
                console.log(Object.keys(selectedApplicantsGraphInDepartments).length);


                if ((Object.keys(selectedApplicantsGraphInDepartments)).length > 0) {
                    const getkeys = Object.entries(selectedApplicantsGraphInDepartments).map(([key, _]) => key);
                    const getvalues = Object.entries(selectedApplicantsGraphInDepartments).map(([_, value]) => value);
                    setLabel(getkeys);
                    setdatavalue(getvalues);
                    console.log("label1:", label);
                    console.log("datas1:", datavalue);
                }

            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }

    useEffect(() => {
        fetchData();
    }, []);

    useEffect(() => {
        console.log("label:", label);
        console.log("dataSeting:", datavalue);
    }, [label, datavalue]);

    return (
        <div style={styles} >
            <Pie data={{ labels: label, datasets: [{ label: "Total Hire", backgroundColor: "rgb(255, 99, 132)", borderColor: "rgb(255, 240, 132)", data: datavalue }] }} />
        </div>
    );
};

export default LineChart;

