import React, { useState, useEffect } from "react";
import Chart from "chart.js/auto";
import { Pie } from "react-chartjs-2";

const LineChart = () => {
    const [label, setLabel] = useState([]);
    const [dataSeting, setdata] = useState([]);

    const fetchData = () => {
        fetch("http://localhost:8080/JobVista/Graphs", {
            method: 'GET',
            headers: {
                'Content-type': 'Application/JSON'
            }
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then(result => {
                const selectedApplicantsGraphInDepartments = result.selectedApplicantsGraphInDepartments;
                console.log(selectedApplicantsGraphInDepartments);
                console.log(Object.keys(selectedApplicantsGraphInDepartments).length);


                if (Object.keys(selectedApplicantsGraphInDepartments).length > 0) {
                    const firstItem = Object.keys(selectedApplicantsGraphInDepartments);
                    const key = Object.keys(firstItem);
                    const value = Object.values(firstItem);
                    setLabel((prevArray) => [...prevArray, key]);
                    setdata((prevArray) => [...prevArray, value]);
                    console.log("label1:", label);
                    console.log("datas1:", dataSeting);
                }

            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }

    useEffect(() => {
        fetchData();
    }, []);

    return (
        <div>
            <Pie data={{ labels: label, dataSeting: [{ label: "My First dataset", backgroundColor: "rgb(255, 99, 132)", borderColor: "rgb(255, 240, 132)", data: dataSeting }] }} />
        </div>
    );
};

export default LineChart;
