import React, { useState, useEffect } from 'react';
import Chart from "chart.js/auto";
import Style from '../BarChart/Bar.module.css';

const HorizontalChart = () => {
    const [label, setLabel] = useState(["1"]);
    const [datavalue, setdatavalue] = useState([5]);

    const fetchData = () => {
        fetch("http://localhost:8080/JobVista/Graphs", {
            method: 'GET',
            headers: {
                'Content-type': 'Application/JSON'
            }
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then(result => {
                const selectedApplicantsGraphInDepartments = result.selectedApplicantsGraphInDepartments;
                console.log(selectedApplicantsGraphInDepartments);
                console.log(Object.keys(selectedApplicantsGraphInDepartments).length);

                if ((Object.keys(selectedApplicantsGraphInDepartments)).length > 0) {

                    const entries = Object.entries(selectedApplicantsGraphInDepartments);
                    if (entries.length === 1) {
                        setLabel(entries.map(([key, _]) => key));
                        setdatavalue(entries.map(([_, value]) => value));
                    } else {
                        const mergedEntry = entries.reduce((acc, [key, value]) => ({ ...acc, [key]: value }), {});
                        setLabel(Object.keys(mergedEntry));
                        setdatavalue(Object.values(mergedEntry));
                    }

                    console.log("label1:", label);
                    console.log("datas1:", datavalue);
                }

            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }

    useEffect(() => {
        fetchData();
    }, []);

    useEffect(() => {
        console.log("label:", label);
        console.log("dataSeting:", datavalue);
    }, [label, datavalue]);

    return (
        <div id={Style.chartId} >
            <HorizontalChart data={{
                    labels: label,
                    datasets: [{
                        label: "Total Hire",
                        data: datavalue,
                        backgroundColor: [
                            "rgba(255, 99, 132, 0.2)",
                            "rgba(54, 162, 235, 0.2)",
                            "rgba(255, 206, 86, 0.2)",
                            "rgba(75, 192, 192, 0.2)",
                            "rgba(153, 102, 255, 0.2)",
                            "rgba(255, 159, 64, 0.2)"
                        ],
                        borderColor: [
                            "rgba(255, 99, 132, 1)",
                            "rgba(54, 162, 235, 1)",
                            "rgba(255, 206, 86, 1)",
                            "rgba(75, 192, 192, 1)",
                            "rgba(153, 102, 255, 1)",
                            "rgba(255, 159, 64, 1)"
                        ],
                        borderWidth: 1
                    }]
                }}
                options={{
                    indexAxis: 'y', 
                    responsive: true
                }}
/>
        </div>
    );
};

export default BarChart;
