import React, { useState, useEffect } from "react";
import Chart from "chart.js/auto";
import { Doughnut } from "react-chartjs-2";
import Style from '../BarChart/Bar.module.css';



// box-sizing: border-box;
// display: block;
// height: 318px;
// width: 754px;


const LineChart = () => {
    const [label, setLabel] = useState(["Finance"]);
    const [datavalue, setdatavalue] = useState([5]);
    const styles = {
        boxSizing: 'border-box',
        display: 'block',
        height: '318px',
        width: '754px',
      };
    const fetchData = () => {
        fetch("http://localhost:8080/JobVista/Graphs", {
            method: 'GET',
            headers: {
                'Content-type': 'Application/JSON'
            }
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then(result => {
                // const selectedApplicantsGraphInDepartments = result.selectedApplicantsGraphInDepartments;
                // console.log(selectedApplicantsGraphInDepartments);
                // console.log(Object.keys(selectedApplicantsGraphInDepartments).length);
                const applicantsStatusGraph = re

                if ((Object.keys(selectedApplicantsGraphInDepartments)).length > 0) {
                    const getkeys = Object.entries(selectedApplicantsGraphInDepartments).map(([key, _]) => key);
                    const getvalues = Object.entries(selectedApplicantsGraphInDepartments).map(([_, value]) => value);

                    setLabel(prevLabel => [...prevLabel, ...getkeys]);
                    setdatavalue(prevDataValue => [...prevDataValue, ...getvalues]);

                    console.log("label1:", label);
                    console.log("datas1:", datavalue);
                }

            })
            .catch(error => {
                console.error('Error fetching data:', error);
            });
    }

    useEffect(() => {
        fetchData();
    }, []);

    useEffect(() => {
        console.log("label:", label);
        console.log("dataSeting:", datavalue);
    }, [label, datavalue]);

    return (
        <div id={Style.chartId} >
            <Doughnut data={{
                    labels: label,
                    datasets: [{
                        label: "Total Hire",
                        data: datavalue,
                        backgroundColor: [
                            "rgba(255, 99, 132, 0.2)",
                            "rgba(54, 162, 235, 0.2)",
                            "rgba(255, 206, 86, 0.2)",
                            "rgba(75, 192, 192, 0.2)",
                            "rgba(153, 102, 255, 0.2)",
                            "rgba(255, 159, 64, 0.2)"
                        ],
                        borderColor: [
                            "rgba(255, 99, 132, 1)",
                            "rgba(54, 162, 235, 1)",
                            "rgba(255, 206, 86, 1)",
                            "rgba(75, 192, 192, 1)",
                            "rgba(153, 102, 255, 1)",
                            "rgba(255, 159, 64, 1)"
                        ],
                        borderWidth: 1
                    }]
                }}
                options={{
                    circumference: 180,
                    rotation: 270
                }}
/>
        </div>
    );
};

export default LineChart;

