import React, { useState } from 'react';
import Style from '../Popup/Popup.module.css';
import { Text } from '../Text/Text';

export const Popup = ({ initialIsSuccess, content }) => {
    const [isSuccessState, setIsSuccessState] = useState(initialIsSuccess);

    return (
        <div id={Style.popupContainer}>
            <div id={Style.iconDiv}>
                {isSuccessState ? (
                    <i className="fa-solid fa-check"></i>
                ) : (
                    <i className="fa-solid fa-exclamation"></i>
                )}
            </div>
            <div id={Style.contentDiv}>
                <Text>{content}</Text>
            </div>
        </div>
    );
};
